const centra = require('centra');
const { MessageEmbed } = require('discord.js')

;(async () => {
    const res = await centra(`https://corona.lmao.ninja/v3/covid-19/countries/${tempVars("country")}`).send()

    const { 
        message,
        cases,
        todayCases,
        deaths,
        todayDeaths,
        recovered,
        todayRecovered,
        active,
        critical,
        countryInfo,
        continent,
        country } = await res.json()

       if (!message) {
        const embed = new MessageEmbed()
        .setTitle(`COVID-19 | ${country} | ${continent}`)
        .setThumbnail(`${countryInfo.flag}`)
        .addFields(
            {name: 'Cases', value: cases},
            {name: 'Today Cases', value: todayCases},
            {name: 'Deaths', value: deaths},
            {name: 'Today Deaths', value: todayDeaths},
            {name: 'Recovered', value: recovered},
            {name: 'Today Recovered', value: todayRecovered},
            {name: 'Active Cases', value: active},
            {name: 'Critical Cases', value: critical}
        );
        msg.channel.send(embed)
        }
        else if (message) {
            msg.channel.send("Uh oh! Country not found or doesn't have any cases");
        }
})()

